import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [currentVideo, setCurrentVideo] = useState(null);
  const [currentInfo, setCurrentInfo] = useState({ country: '', genre: '' });

  const shuffleVideo = async () => {
    const response = await fetch('http://localhost:5000/genres');
    const data = await response.json();
    const randomVideo = data.videos[Math.floor(Math.random() * data.videos.length)];
    setCurrentVideo(randomVideo);
    setCurrentInfo({ country: data.country, genre: data.genre });
  };

  useEffect(() => {
    shuffleVideo();
  }, []);

  return (
      <div className="App">
        <header className="App-header">
          <h1>Shuffle Worst Genres</h1>
          <button onClick={shuffleVideo}>Shuffle Song</button>
          {currentVideo && (
              <div>
                <h2>{currentInfo.country} - {currentInfo.genre}</h2>
                <iframe
                    width="560"
                    height="315"
                    src={currentVideo}
                    title="YouTube video player"
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                ></iframe>
              </div>
          )}
        </header>
      </div>
  );
}

export default App;
